INSERT INTO `post` VALUES (76, 4, 'ผลบอล ปาร์ม่า 1-3 โรม่า', 'ฟรานเชสโก้ ต๊อตตี้ เจ้าชายหมาป่ายังรับบทพระเอกเหมือนเคยยิงประตูสำคัญช่วย หมาป่า โรม่า ตามอยู่ในครี่งแรกพลิกแซงทีเดียวต้อน "จัลโล่บลู" ปาร์ม่า 3-1 พร้อมทำสถิติชนะ 3 นัดรวดครองจ่าฝูงร่วมกับ นาโปลี', '<div align=\\"center\\" style=\\"font-family: Tahoma; font-size: 12px; text-align: -webkit-center;\\"><img src=\\"http://image.hugball.com/Pic/Match/12359_1.jpg\\" width=\\"300\\" height=\\"200\\"></div><div align=\\"left\\" style=\\"font-family: Tahoma; font-size: 12px; margin-left: 10px; margin-right: 10px;\\">&nbsp;&nbsp;&nbsp;&nbsp;<p>&nbsp;<span style=\\"color: rgb(249, 91, 11); font-size: 20px; line-height: 2.2;\\">ต๊อตตี้ช่วยท่านได้!หมาป่า100%พลิกแซงจัลโล่บลู 3-1 ขึ้นทาบฝูง</span></p><div class=\\"post_head\\" style=\\"margin: 0px; padding: 0px; width: 1089px; font-size: 13px;\\"><div style=\\"margin: 0px 10px 0px 0px; padding: 0px; float: right;\\"><a target=\\"_blank\\" href=\\"http://www.soccersuck.com/users/blog/53034\\" style=\\"color: rgb(51, 51, 51); text-decoration: none; margin: 0px; padding: 0px; border: none;\\"><div class=\\"button_profile\\" title=\\"ข้อมูลส่วนตัว\\" style=\\"margin: 7px -1px; padding: 0px; background-image: url(http://www.soccersuck.com/img/web/ss_broad_st2_bt_profile.png); width: 70px; height: 36px; float: right; cursor: pointer;\\">&nbsp;</div></a><div class=\\"clear\\" style=\\"margin: 0px; padding: 0px; clear: both;\\">&nbsp;</div></div><div class=\\"clear\\" style=\\"margin: 0px; padding: 0px; clear: both;\\">&nbsp;</div></div><div class=\\"post_desc\\" style=\\"margin: 0px; padding: 10px; min-height: 130px; color: rgb(85, 85, 85); font-size: 17px; word-wrap: break-word;\\"><span style=\\"margin: 0px; padding: 0px; font-size: 18px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: blue;\\">ต๊อตตี้ช่วยท่านได้!หมาป่า100%พลิกแซงจัลโล่บลู 3-1 ขึ้นทาบฝูง</span></strong><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\">ฟรานเชสโก้ ต๊อตตี้ เจ้าชายหมาป่ายังรับบทพระเอกเหมือนเคยยิงประตูสำคัญช่วย \\"หมาป่า\\" โรม่า ตามอยู่ในครี่งแรกพลิกแซงทีเดียวต้อน \\"จัลโล่บลู\\" ปาร์ม่า 3-1 พร้อมทำสถิติชนะ 3 นัดรวดครองจ่าฝูงร่วมกับ นาโปลี<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: rgb(139, 0, 0);\\"><strong style=\\"margin: 0px; padding: 0px;\\">ผลกัลโช่ เ ซเรีย อา<br style=\\"margin: 0px; padding: 0px;\\">วันจันทร์ที่ 16 กันยายน 2556&nbsp;</strong></span><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: blue;\\">ปาร์ม่า<img src=\\"http://d.yimg.com/i/ng/sp/eurosport/41x50/121749687774.gif\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\">&nbsp;1-3&nbsp;<img src=\\"http://d.yimg.com/i/ng/sp/eurosport/39x50/121749687776.gif\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\">โรม่า<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\">ประตู:</span></strong>1-0 โจนาธาน เบียเบียนี่ น.39,1-1 อเลสซานโดร ฟลอเรนซี่ น.47,1-2 ฟรานเชสโก้ ต๊อตตี้ น.70,1-3 เควิน สตรูทมัน(จุดโทษ) น.85<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: rgb(139, 0, 0);\\"><strong style=\\"margin: 0px; padding: 0px;\\">ครึ่งแรก</strong></span><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">ต๊อตตี้ทักทาย</strong></span><br style=\\"margin: 0px; padding: 0px;\\">เปิดฉากแค่ 4 นาทีแรกเท่านั้น \\"จัลโล่รอสซี่\\" พยายามฉวยโอกาสโจมตีเร็วแบบไม่ยอมให้ตั้งตัว ต๊อตตี้ ตะบันเต็มข้อเดือดร้อน มิรานเต้ นายด่านจัลโล่บลูต้องออกแรงปัดทิ้งข้ามคาน&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">ระวัง!ไอ้หน้าสิวเกือบยิงทีมเก่า</strong></span><br style=\\"margin: 0px; padding: 0px;\\">บอลเปิดแลกกันเลยอีก 3 นาทีถัดมา \\"จัลโล่บลู\\" ตอบโต้บ้าง มาร์คิออนนี่ ไหลให้ คาสซาโน่ วางเท้ายิงเน้นๆนอกกรอบเขตโทษ 20 หลาบอลถากเสาออกไปนิดเดียว&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">ต๊อตตี้ยังยิงไม่ผ่าน</strong></span><br style=\\"margin: 0px; padding: 0px;\\">เกมค่อนข้างสูสีผลักกันบุกจนกระทั่งนาที 20 \\"หมาป่า\\" อาศัยการต่อบอลเป็นทอดๆเกือบได้ผลเริ่มจาก ปานิช ผ่าน สตรูทมัน ส่งต่อ ลายิช ไหลให้ ต๊อตี้ จบสกอร์แต่ไม่ผ่านนายทวาร มิรานเต้&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">เบียเบียนี่ได้ลุ้น</strong><br style=\\"margin: 0px; padding: 0px;\\"></span><br style=\\"margin: 0px; padding: 0px;\\">อีก 5 นาทีถัดมา ปาร์ม่า ได้ลุ้นบ้าง คาสซาโน่ ไหลให้ เบียเบียนี่ แหวกฝ่าแนวรับกระชากเข้าไปยิงแต่ยังไม่ผ่าน เด ซังค์ติส นายด่านจัลโล่บลูยังเหนียวเซฟเอาไว้ได้<img src=\\"http://image.ohozaa.com/i/0fa/MibSzn.jpg\\" border=\\"0\\" hspace=\\"10\\" vspace=\\"5\\" align=\\"right\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">จัลโล่บลูเฮจนได้!เบียเบียโน่โขกนำ 1-0</strong></span><br style=\\"margin: 0px; padding: 0px;\\">เข้าสู่นาที 39 กองเชียร์เจ้าถิ่นสมหวังเสียทีได้ประตูขึ้นนำ 1-0 คาสซานี่ ตั้งป้อมโยนจากริมเส้นทางขวาโค้งเข้าไปในกรอบเขตโทษเสาแรก เบียเบียนี่ เทคตัวสะบัดโหม่งเสียบตาข่าย&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">เจ้าถิ่นคึก!เบียเบียนี่เกือบเบิ้ล</strong></span><br style=\\"margin: 0px; padding: 0px;\\">พอได้ประตูขึ้นนำสมใจ ปาร์ม่า คึกใหญ่เดินหน้ากดดันต่อเนื่องก่อนพักเบรก 2 นาที เกือบทิ้งห่างสองเม็ด คาสซานี่ ทำชิ่งจังหวะเดียวให้ เบียเบียนี่ หลุดเข้าไปในกรอบเขตโทษทางขวายิงหักข้อมากเกินไปหนีมือนายทวาร เด ซังค์ติส ก่อนผ่านหน้าปากประตูออกไป จบครึ่งแรก \\"จัลโล่บลู\\" กุมความได้เปรียบ 1 เม็ด<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: rgb(139, 0, 0);\\"><strong style=\\"margin: 0px; padding: 0px;\\">ครึ่งหลัง</strong></span><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">หมาป่าเอาคืน!ฟลอเรนซี่ยิงสุดสวย</strong></span><br style=\\"margin: 0px; padding: 0px;\\">กลับมาเริ่มต้นกันใหม่ \\"หมาป่า\\" ลุยใส่ทันทีเล่นงานแนวรับเจ้าถิ่นไม่ทันตั้งตั้วงแคต่นาทีเดียวตามตีเสมอ 1-1 ปานิช โชว์คิลเลอร์พาสชิพเข้าไปในกรอบเขตโทษทางขวา ฟลอเรนซี่ วางเท้ายิงแบบไม่ต้องรอให้บอลตกลงพื้นพุ่งเสียบตาข่ายอย่างสุดสวย&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">อูย!หมาป่าหวิดเน่าอีก</strong></span><br style=\\"margin: 0px; padding: 0px;\\">ตามตีเสมอไวัเหลือเกินทำให้ \\"จัลโล่บลู\\" ต้องหันมาเปิดเกมบุกใหม่อีกครั้งนาที 52 เกือบได้เฮ คาสซาโน่ หลุดเข้าไปถึงเส้นหลังในเขตโทษทางซ้ายก่อนโยนเข้ากลางกองหลังหมาป่าสกัดทิ้งมาเข้าทางปืน มาร์คิออนนี่ ยิงบริเวณหัวกระโหลก อเมารี สกัดลูกหลังบอลถากเสาออกไปอย่างน่าเสียดาย&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">เขาล่ะ!ต๊อตตี้นิ่งมากยิงแซง 2-1</strong></span><br style=\\"margin: 0px; padding: 0px;\\">เกมก่ำกึ่งดูไม่ออกใครดีกว่ากันจนกระทั่งนาที 70 กลายเป็น \\"จัลโล่รอสซี่\\" พลิกสถานการณ์แซง 2-1 ต้องชมการจ่ายอันเหนือชั้นของ สตรูทมัน ชิพข้ามแนวรับทั้งกระบิ ต๊อตตี้ หลุดกับด้กล้ำหน้าโล่งๆเอาบอลลงก่อนอาศัยความนิ่งรอจังหวะแล้วง้างเท้ายิงผ่านนายทวาร มิรานเต้ เข้าไปไม่เหลือ<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">หมาป่าคึกใหญ่เกือบได้เม็ดสาม</strong></span><br style=\\"margin: 0px; padding: 0px;\\">หลังยิงสองประตูรวดดูเหมือนเกมตกอยู่ในคอนโทรลของ \\"จัลโลรอสซี่\\" คุมสถานการณ์ไว้ได้นาที 80 ปานิช ซัดฟรีคิกทางซ้ายระยะ 20 หลาบอลทะลุกำแพง มิรานเต้ นายทวารปัดทิ้งออกมา คาสตาน พยายามซ้ำดาบสองล้ำหน้าไปก่อน&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">เจ้าบ้านเซ็งอดเจ๊า!เจอสกัดจากเส้น</strong></span><br style=\\"margin: 0px; padding: 0px;\\">\\"จัลโล่บลู\\" ในฐานะเจ้าถิ่นพยายามฮึดสู้นาที 84 พลาดโอกาสทองอย่างน่าเสียดาย คาสซาโน่ กึ่งยิงกึ่งผ่านบอลกำลังจะเสียบตาข่ายแต่ บอร์ริเอลโล่ ช่วยสกัดพ้นอันตราย<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\"><strong style=\\"margin: 0px; padding: 0px;\\">แชร์วินโญ่เรียกจุดโทษ!สตรูทมันยิงเป้าฝัง 3-1</strong></span><br style=\\"margin: 0px; padding: 0px;\\">จากที่ \\"จัลโล่บลู\\" เกือบตีเสมอพอทำไม่ได้กลายเป็นเสียประตูในอีกนาทีถัดมา แชร์วินโญ่ ตัวสำรองหลุดเข้าไปในกรอบเขตโทษโดน คาสซานี่ สกัดจากด้านหลัง ผู้ตัดสินเป่าเป็นจุดโทษทันที ต๊อตตี้ โดนเปลี่ยนออกไปแล้ว สตรูทมัน รับหน้าที่เพชฌฆาตซัดเข้าไปทิ้งห่าง 3-1 ก่อนเวลาที่เลหือ โรม่า คุมสถานการณ์ไว้ได้ชนะด้วยสกอร์ดังกล่าว<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: blue;\\">รายชื่อผู้เล่นทั้งสองทีม<br style=\\"margin: 0px; padding: 0px;\\">ปาร์ม่า(3-5-2):&nbsp;</span></strong>มิรานเต้-คาสซานี่,เฟลิเป้,ลูคาเรลลี่-เบียเบียนี่<img src=\\"http://i2.goal.com/web/goal/2013.09.16-v1/images/default/match/g1big.gif\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\">(ซานโนเน่ น.79),การ์กาโน่,มาร์คิออนนี่,ปาโรโล่,กอบบี้(เมสบาห์ น.90)-คาสซาโน่,อเมารี(โอกาก้า น.69)<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: blue;\\">โรม่า(4-3-3)</span></strong>: เดอ ซังค์คิส-ไมค่อน,เบนาเตีย,คาสตาน,บัลซาเร็ตติ-ปานิช,เด รอสซี่,สตรูทมัน<img src=\\"http://i1.goal.com/web/goal/2013.09.16-v1/images/default/match/pbig.gif\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\">-ฟลอเรนซี่<img src=\\"http://i2.goal.com/web/goal/2013.09.16-v1/images/default/match/g1big.gif\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\">(มาร์กินโญ่ น.69),ต๊อตตี้<img src=\\"http://i2.goal.com/web/goal/2013.09.16-v1/images/default/match/g1big.gif\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\">(บอร์ริเอลโล่ น.76),ลายิช(แชร์วินโญ่ น.54)&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><a target=\\"_blank\\" href=\\"http://image.ohozaa.com/view2/x5HPiEkBnxFS5MM1\\" rel=\\"nofollow\\" title=\\"http://image.ohozaa.com/view2/x5HPiEkBnxFS5MM1\\" style=\\"color: rgb(51, 51, 51); text-decoration: none; margin: 0px; padding: 0px; border: none;\\"><img src=\\"http://image.ohozaa.com/i/a21/zPPVqv.JPG\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"></a><br style=\\"margin: 0px; padding: 0px;\\"><a target=\\"_blank\\" href=\\"http://image.ohozaa.com/view2/x5HPjIgEecF41do9\\" rel=\\"nofollow\\" title=\\"http://image.ohozaa.com/view2/x5HPjIgEecF41do9\\" style=\\"color: rgb(51, 51, 51); text-decoration: none; margin: 0px; padding: 0px; border: none;\\"><img src=\\"http://image.ohozaa.com/i/60c/sWohYC.JPG\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"></a><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><img src=\\"http://cdn.corrieredellosport.it/images/84/C_3_Media_1787084_immagine_obig.jpg\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><a target=\\"_blank\\" href=\\"http://image.ohozaa.com/view2/x5HUShBxQLtmDkzo\\" rel=\\"nofollow\\" title=\\"http://image.ohozaa.com/view2/x5HUShBxQLtmDkzo\\" style=\\"color: rgb(51, 51, 51); text-decoration: none; margin: 0px; padding: 0px; border: none;\\"><img src=\\"http://image.ohozaa.com/i/552/9W70zE.jpg\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"></a><br style=\\"margin: 0px; padding: 0px;\\"><a target=\\"_blank\\" href=\\"http://image.ohozaa.com/view2/x5HURdFuZW0ygK1X\\" rel=\\"nofollow\\" title=\\"http://image.ohozaa.com/view2/x5HURdFuZW0ygK1X\\" style=\\"color: rgb(51, 51, 51); text-decoration: none; margin: 0px; padding: 0px; border: none;\\"><img src=\\"http://image.ohozaa.com/i/592/EVqtMS.jpg\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"></a></span></div></div>', '2013919154507-02.jpg', 1, '2013-09-19 15:45:07', 2);
INSERT INTO `post` VALUES (77, 4, 'กาก้า ประกาศไม่รับค่าเหนื่อยช่วงรักษาตัว', 'ริคาร์โด้ กาก้าเพลย์เมคเกอร์บราซิเลี่ยนขอเอซี มิลานได้ใจแฟนบอลไปเต็มๆ หลังไม่ขอรับเงินค่าเหนื่อยในช่วงที่พักรักษาตัวจากอาการบาดเจ็บอยู่ในตอนนี้', '<div align=\\"center\\" style=\\"font-family: Tahoma; font-size: 12px; text-align: -webkit-center;\\"><img src=\\"http://image.hugball.com/Pic/Match/12374_1.jpg\\" width=\\"300\\" height=\\"200\\"></div><div align=\\"left\\" style=\\"font-family: Tahoma; font-size: 12px; margin-left: 10px; margin-right: 10px;\\">&nbsp;&nbsp;&nbsp;&nbsp;<p><span style=\\"font-size: larger;\\">ริคาร์โด้ กาก้าเพลย์เมคเกอร์บราซิเลี่ยนขอเอซี มิลานได้ใจแฟนบอลไปเต็มๆ หลังไม่ขอรับเงินค่าเหนื่อยในช่วงที่พักรักษาตัวจากอาการบาดเจ็บอยู่ในตอนนี้<br><br>กาก้าได้รับบาดเจ็บในระหว่างการเล่นเกมเซเรีย อาเมื่อวันเสาร์ ซึ่งคาดกันว่าเขาอาจต้องพักนานถึง 1 เดือน แต่เจ้าตัวแสดงสปิริตไม่ขอรับค่าเหนื่อยในช่วงนี้<br><br>\\"ผมไม่ต้องการอะไรจากมิลาน นอกจากความรักและการสนับสนุนจนกระทั่งผมดีขึ้น&nbsp; ด้วยเหตุผลดังกล่าว ผมตัดสินใจแล้วว่าจะไม่ขอรับเงินค่าเหนื่อยของผมในช่วงเวลานี้\\"</span></p></div>', '2013919154541-03.jpg', 1, '2013-09-19 15:45:41', 0);
INSERT INTO `post` VALUES (78, 4, 'วิโอล่าหวังแซงปืนเซ็นฟรี มาริก้า เสริมคม', 'ฟิออเรนติน่าหวังปาดหน้าอาร์เซน่อลเข้าลากซิเปรียน มาริก้าหัวหอกฟรีเอเยนต์มาร่วมทีม ภายหลังต้องสังเวยมาริโอ โกเมซไปด้วยอาการบาดเจ็บ', '<div align=\\"center\\" style=\\"font-family: Tahoma; font-size: 12px; text-align: -webkit-center;\\"><img src=\\"http://image.hugball.com/Pic/Match/12377_1.jpg\\" width=\\"300\\" height=\\"200\\"></div><div align=\\"left\\" style=\\"font-family: Tahoma; font-size: 12px; margin-left: 10px; margin-right: 10px;\\">&nbsp;&nbsp;&nbsp;&nbsp;<p><span style=\\"font-size: larger;\\">ฟิออเรนติน่าหวังปาดหน้าอาร์เซน่อลเข้าลากซิเปรียน มาริก้าหัวหอกฟรีเอเยนต์มาร่วมทีม ภายหลังต้องสังเวยมาริโอ โกเมซไปด้วยอาการบาดเจ็บ<br><br>กองหน้าทีมชาติโรมาเนียเพิ่งแยกทางจากชาลเก้เมื่อช่วงซัมเมอร์ที่ผ่านมา และก่อนหน้านี้เอเยนต์ของเขาก็อ้างว่านักเตะกำลังจะย้ายไปร่วมทัพ\\"ปืนใหญ่\\"<br><br>อย่างไรก็ดีหลังต้องเสียโกเมซไปด้วยอาการเอ็นหัวเข่าฉีกขาดจนต้องพักยาวถึง 7 เดือน ทำให้ฟิออเรนติน่าเตรียมโดดมาร่วมวงล่ามาริก้าด้วยอีกทีม<br><br>นอกจากมาริก้าแล้ว \\"วิโอล่า\\"ยังสนใจที่จะเซ็นสัญญากับตอมมาโซ รอคคี่อดีตศูนย์หน้าของอินเตอร์ มิลานและลาซิโอที่เป็นนักเตะไร้สังกัดอยู่ในปัจจุบัน</span></p></div>', '2013919154633-04.jpg', 1, '2013-09-19 15:46:25', 0);
INSERT INTO `post` VALUES (79, 4, 'โรม่าหวังสู่ขอ คากาวะ ชิชาริโต้ จากผี', 'อาแอส โรม่าแสดงความสนใจที่อยากได้ชินจิ คากาวะและฮาเวียร์ เอร์นานเดซสองตัวรุกสำรองของแมนเชสเตอร์ ยูไนเต็ดมาเสริมทัพในช่วงเปิดตลาดรอบใหม่', '<div align=\\"center\\" style=\\"font-family: Tahoma; font-size: 12px; text-align: -webkit-center;\\"><img src=\\"http://image.hugball.com/Pic/Match/12380_1.jpg\\" width=\\"300\\" height=\\"200\\"></div><div align=\\"left\\" style=\\"font-family: Tahoma; font-size: 12px; margin-left: 10px; margin-right: 10px;\\">&nbsp;&nbsp;&nbsp;&nbsp;<p><span style=\\"font-size: larger;\\">อาแอส โรม่าแสดงความสนใจที่อยากได้ชินจิ คากาวะและฮาเวียร์ เอร์นานเดซสองตัวรุกสำรองของแมนเชสเตอร์ ยูไนเต็ดมาเสริมทัพในช่วงเปิดตลาดรอบใหม่<br><br>ทั้งสองคนแทบไม่ได้รับโอกาสจากเดวิด มอยส์กุนซือ\\"ปีศาจแดง\\"ให้ลงเล่นในฤดูกาลนี้ โดยเฉพาะในรายของคากาวะที่ไม่ได้ลงสนามให้ทีมแม้แต่นาทีเดียว&nbsp;<br><br>ด้วยเหตุนี้เลยเปิดช่องให้\\"จัลโล่รอสซี่\\"เตรียมเจรจากับ\\"ปีศาจแดง\\"เพื่อขอซื้อคากาวะกับเอร์นานเดซมาเป็นตัวแทนของเอริค ลาเมลาและปาโบล ออสวัลโด้ที่ย้ายออกจากทีมในช่วงซัมเมอร์<br><br>เชื่อกันว่า\\"หมาป่าแห่งกรุงโรม\\"พร้อมทุ่มเงินถึง 35 ล้านปอนด์เพื่อไถ่ตัวคากาวะกับชิชาริโต้ แต่โอกาสได้ตัวคงไม่ง่ายเพราะ\\"ปีศาจแดง\\"ยังไม่ได้หาตัวแทนของทั้งคู่เตรียมไว้เลย</span></p></div>', '2013919154715-05.jpg', 1, '2013-09-19 15:47:15', 0);
INSERT INTO `post` VALUES (80, 4, 'ปาโต้ อัดทีมแพทย์มิลานเกือบทำอนาคตเขาดับ', 'อเล็กซานเดร ปาโต้กองหน้าตัวเก่งของโครินเธียนส์วิจารณ์การทำงานของทีมแพทย์เอซี มิลานว่าเป็นเหตุผลสำคัญที่ทำให้เขาไม่สามารถโชว์ฟอร์มได้ดีในการเล่นที่ยุโรป', '<div align=\\"center\\" style=\\"font-family: Tahoma; font-size: 12px; text-align: -webkit-center;\\"><img src=\\"http://image.hugball.com/Pic/Match/12403_1.jpg\\" width=\\"300\\" height=\\"200\\"></div><div align=\\"left\\" style=\\"font-family: Tahoma; font-size: 12px; margin-left: 10px; margin-right: 10px;\\">&nbsp;&nbsp;&nbsp;&nbsp;<p><span style=\\"font-size: larger;\\">อเล็กซานเดร ปาโต้กองหน้าตัวเก่งของโครินเธียนส์วิจารณ์การทำงานของทีมแพทย์เอซี มิลานว่าเป็นเหตุผลสำคัญที่ทำให้เขาไม่สามารถโชว์ฟอร์มได้ดีในการเล่นที่ยุโรป<br><br>ดาวยิงทีมชาติบราซิลมีอาการบาดเจ็บติดเสมอตลอดระยะเวลา 7 ปีที่ซาน ซิโร่ แต่เมื่อเขากลับมาเล่นที่นบ้านเกิดกลับฟิตสมบูรณ์และทำผลงานได้ดีอย่างต่อเนื่องจนถูกเรียกตัวติดทีมชาติอีกครั้ง<br><br>\\"ผมมีปัญหากล้ามเนื่อมาโดยตลอด ผมได้รับบาดเจ็บมากมายสมัยค้าแข้งที่มิลานและเมื่อผมได้กลับไปลงสนามผมก็ได้รับบาดเจ็บอีกครั้งทั้งที่เพิ่งฟื้นตัว\\"<br><br>\\"มันเป็นความผิดของแพทย์ที่มิลาน ที่ชอบบังคับให้ผมฝืนอาการบาดเจ็บกลับมาลงสนามเร็วเกินไป\\"</span></p></div>', '2013919154747-06.jpg', 1, '2013-09-19 15:47:47', 5);
INSERT INTO `post` VALUES (81, 5, 'คาห์นเชื่อหมดยุคบาร์ซ่ามี เสือใต้ แทนที่', 'โอลิเวอร์ คาห์นตำนานผู้รักษาประตูเยอรมนีเชื่อว่ายุคมองของบาร์เซโลน่าใกล้สิ้นสุดลงแล้ว และยุคต่อไปจะเป็นทีของบาเยิร์น มิวนิค', '<div align=\\"center\\" style=\\"font-family: Tahoma; font-size: 12px; text-align: -webkit-center;\\"><img src=\\"http://image.hugball.com/Pic/Match/12262_1.jpg\\" width=\\"300\\" height=\\"200\\"></div><div align=\\"left\\" style=\\"font-family: Tahoma; font-size: 12px; margin-left: 10px; margin-right: 10px;\\">&nbsp;&nbsp;&nbsp;&nbsp;<p><span style=\\"font-size: larger;\\">โอลิเวอร์ คาห์นตำนานผู้รักษาประตูเยอรมนีเชื่อว่ายุคมองของบาร์เซโลน่าใกล้สิ้นสุดลงแล้ว และยุคต่อไปจะเป็นทีของบาเยิร์น มิวนิค<br><br>\\"อาซูลกราน่า\\"ต้องตกรอบรองชนะเลิศแชมเปี้ยนส์ลีกซีซั่นก่อนด้วยการพ่ายบาเยิร์น 7-0 โดยที่ยอดทีมแคว้นบาวาเรียคว้าสามแชมป์ได้สำเร็จ ทำให้คาห์นมองว่าต่อจากนี้จะเป็นยุคของ\\"เสือใต้\\"<br><br>\\"ผมเชื่อว่ายุคของบาร์เซโลนาได้จบลงแล้ว ผมกล้าพูด ณ ตรงนี้เลยว่าทีมจากเยอรมันไม่เคยกลัวทีมไหน ผมคิดว่าการเล่นของบาเยิร์น เมื่อปีก่อนถือว่าเป็นตัวพิสูจน์อย่างดี\\"<br><br>\\"บาเยิร์นจำเป็นต้องประสบความสำเร็จอย่างต่อเนื่องเพื่อแสดงให้เห็นว่ายุคสมัยได้เปลี่ยนไปแล้ว\\"</span></p></div>', '2013919155014-01.jpg', 1, '2013-09-19 15:50:14', 0);
INSERT INTO `post` VALUES (82, 5, 'ผลบอล แฮร์ธ่า เบอร์ลิน 0-1 สตุ๊ตการ์ท', 'คริสเตียน เกนท์เนอร์ สวมบทฮีโร่โหม่งประตูชัย นำ ม้าขาว สตุทการ์ท บุกมาโค่น หญิงชรา แฮร์ธา เบอร์ลิน คาบ้าน 1-0 ศึกฟุตบอล บุนเดสลีกา เยอรมัน เมื่อคืนวันศุกร์ที่ 13 กันยายนที่ผ่านมา', '<div align=\\"center\\" style=\\"font-family: Tahoma; font-size: 12px; text-align: -webkit-center;\\"><img src=\\"http://image.hugball.com/Pic/Match/12283_1.jpg\\" width=\\"300\\" height=\\"200\\"></div><div align=\\"left\\" style=\\"font-family: Tahoma; font-size: 12px; margin-left: 10px; margin-right: 10px;\\">&nbsp;&nbsp;&nbsp;&nbsp;<p>&nbsp;<span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;คริสเตียน เกนท์เนอร์ สวมบทฮีโร่โหม่งประตูชัย นำ \\"ม้าขาว\\" สตุทการ์ท บุกมาโค่น \\"หญิงชรา\\" แฮร์ธา เบอร์ลิน คาบ้าน 1-0 ศึกฟุตบอล บุนเดสลีกา เยอรมัน เมื่อคืนวันศุกร์ที่ 13 กันยายนที่ผ่านมา</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;	</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;	ศึกฟุตบอล บุนเดสลีกา เยอรมัน ฤดูกาล 2013-14 นัดที่ 5 แฮร์ธา เบอร์ลิน ทีมน้องใหม่ ที่กำลังฟอร์มดี แพ้ 1 จาก 4 นัด เปิดสนามโอลิมปิก สเตเดียม รับการมาเยือน สตุทการ์ท ซึ่งหล่นอยู่ท้ายตาราง เก็บเพียง 3 แต้ม จาก 4 นัด เท่านั้น</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;	เสียงนกหวีดดังขึ้น แฮร์ธา เบอร์ลิน บดต่อเนื่องจนเกือบขึ้นนำนาที 18 โยฮันเนส ฟาน เดน เบิร์ก ทุ่มจากซ้ายมาเสาแรก เอเดรียน รามอส แตะจังหวะเดียวมาให้ เอนิส เบน-ฮาติรา ตะบันเต็มข้อจากเขตโทษด้านซ้าย สเวน อุลรีช ทุบออกหลัง ต่อมา เจ้าถิ่น ทำเกมบุกได้น้ำได้เนื้อกว่า แต่ยังไม่มีโอกาสเงื้อเท้ายิงแบบจะแจ้ง เข้าสู่นาที 40 สตุทการ์ท ได้เสียวจากลูกฟรีคิก อเล็กซานดรู มักซิม เปิดจากขวามาให้ คริสเตียน เกนท์เนอร์ วิ่งมาสะบัดเสาแรก โธมัส คราฟท์ ผวาปัดข้ามคาน จบครึ่งแรก เสมอกัน 0-0</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;	ครึ่งหลังเปิดฉากเพียง 4 นาที \\"ม้าขาว\\" ที่เริ่มจูนเครื่องติด ขึ้นนำจากลูกเตะมุมฝั่งซ้าย อเล็กซานดรู มักซิม เปิดโค้งมาเสาแรก คริสเตียน เกนท์เนอร์ โหม่งเช็ด บอลพุ่งเข้าแสกหน้า โธมัส คราฟท์ เสียบมุมเสาไกล จากนั้นนาที 51 น้องใหม่ หวิดตีเสมอทันควัน เอเดรียน รามอส เปิดเรียดจาดซ้ายมาให้ แพร์ สเคลเบิร์ด ชาร์จกลางประตู สเวน อุลรีช ทิ้งตัวไปด้านซ้าย แต่ยังยืดแขนป้องกันไว้ได้</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;	แฮร์ธาฯ เดินเครื่องเต็มสูบ ก่อนพลาดโอกาสทองนาที 65 รอนนี จ่ายออกทางซ้ายให้ โยฮันเนส ฟาน เดน เบิร์ก สปีดเข้าเขตโทษ ก่อนยิงยัดเสาแรก สเวน อุลรีช ปัดออกหลัง ต่อมา 1 นาที รอนนี เปิดเตะมุมฝั่งซ้ายมาให้ จอห์น แอนโธนี บรูกส์ ขึ้นโหม่งกลางประตู อุลรีช เทกตัวปัดข้ามคาน ช่วงเวลาที่เหลือ \\"หญิงชรา\\" แทบพับสนามบุก ทว่าแนวรับ ทีมเยือน ต้านทานอยู่ จบเกม สตุทการ์ท เอาชนะไป 1-0</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;รายชื่อ 11 ตัวจริง</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;แฮร์ธาฯ : โธมัส คราฟท์ , ปีเตอร์ เปการิก , โยฮันเนส ฟาน เดน เบิร์ก , จอห์น แอนโธนี บรูกส์ , ฟาเบียน ลุสเทนแบร์เกอร์ , ฮาจิเมะ โฮโซกาอิ , เอนิส เบน-ฮาติรา , รอนนี , แพร์ สเคลเบรด , โทลกา ซิเกอร์ซี , เอเดรียน รามอส</span><br style=\\"color: rgb(102, 102, 102); font-size: 14px;\\"><span style=\\"color: rgb(102, 102, 102); font-size: 14px;\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;สตุทการ์ท : สเวน อุลรีช , โกโตคุ ซากาอิ , ดาเนียล ชวาบ , อาร์เธอร์ โบกา , อันโตนิโอ รูดิเกอร์ , วิลเลียม ควิสต์ , โมริตซ์ ไลท์เนอร์ , ทิโม แวร์เนอร์ , คริสเตียน เกนท์เนอร์ , อเล็กซานดรู มักซิม , เวดัด อิบิเซวิช</span></p></div>', '2013919155052-02.jpg', 1, '2013-09-19 15:50:52', 0);
INSERT INTO `post` VALUES (83, 5, 'เฮอร์เนสบอกคิดถูกแล้วตั้ง เป๊ป มาคุมเสือ', 'อูลี่ เฮอร์เนสประธานสโมสรบาเยิร์น มิวนิคมั่นใจว่าตนเองคิดไม่ผิดที่เลือกเป๊ป กวาร์ดิโอล่าเข้ามาสานงานคุมทีมของจุ๊ปป์ ไฮน์เกสในฤดูกาลนี้', '<div align=\\"center\\" style=\\"font-family: Tahoma; font-size: 12px; text-align: -webkit-center;\\"><img src=\\"http://image.hugball.com/Pic/Match/12296_1.jpg\\" width=\\"300\\" height=\\"200\\"></div><div align=\\"left\\" style=\\"font-family: Tahoma; font-size: 12px; margin-left: 10px; margin-right: 10px;\\">&nbsp;&nbsp;&nbsp;&nbsp;<p><span style=\\"font-size: larger;\\">อูลี่ เฮอร์เนสประธานสโมสรบาเยิร์น มิวนิคมั่นใจว่าตนเองคิดไม่ผิดที่เลือกเป๊ป กวาร์ดิโอล่าเข้ามาสานงานคุมทีมของจุ๊ปป์ ไฮน์เกสในฤดูกาลนี้<br><br>เป๊ปก้าวมารับตำแหน่งเทรนเนอร์\\"เสือใต้\\"ต่อจากไฮน์เกสที่พาทีมคว้าเทรเบิ้ลแชมป์เมื่อซีซั่นก่อนซึ่งเฮอร์เนสเชื่อว่าเป็นการตัดสินใจที่ถูกต้องแล้ว<br><br>\\"จุ๊ปป์ ไฮน์เกสเป็นคนตัดสินใจเรื่องนี่เองที่จะวางมือจากการคุมทีมและผมคิดว่าเราตัดสินใจถูกต้องแล้วที่เลือกเป๊ป กวาร์ดิโอล่าเข้ามาแทนที่เขา\\"&nbsp;<br><br>\\"เป๊ปพิสูจน์ให้เราเห็นแล้วในเกมยูฟ่า ซูเปอร์คัพ ผมคิดว่าทุกคนคงสนุกมากที่ได้ร่วมงานกับเขา การรั้งอันดับ 1 เหนือทุกทีมบนโลกไปนี้เป็นเวลานานเป็นเรื่องที่ยาก แต่เราจะพยายามทำให้ที่ดีที่สุด\\"</span></p></div>', '2013919155125-03.jpg', 1, '2013-09-19 15:51:25', 0);
INSERT INTO `post` VALUES (84, 5, 'ผลบอล บาเยิร์น มิวนิค 2-0 ฮันโนเวอร์ 96', 'บาเยิร์น ที่เกือบเอาตัวไม่รอดในครึ่งแรกมาได้สองลูกในครึ่งหลังจากมาริโอ มานด์ซูคิชและฟร็องค์ ริเบรี่ เอาชนะฮันโนเวอร์ไปได้ 2-0 แซงโบรุสเซีย ดอร์ทมุนด์ ขึ้นไปนำเป็นจ่าฝูงชั่วคราวก่อน', '<div align=\\"center\\" style=\\"font-family: Tahoma; font-size: 12px; text-align: -webkit-center;\\"><img src=\\"http://image.hugball.com/Pic/Match/12300_1.jpg\\" width=\\"300\\" height=\\"200\\"></div><div align=\\"left\\" style=\\"font-family: Tahoma; font-size: 12px; margin-left: 10px; margin-right: 10px;\\">&nbsp;&nbsp;&nbsp;&nbsp;<p>&nbsp;<span style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 18px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: blue;\\">เด็ดขาดกว่า!พี่เสือกัดฟันเชือดฮันโนเวอร์ 2-0 ขึ้นจ่าฝูงก่อน</span></strong><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\">บาเยิร์น ที่เกือบเอาตัวไม่รอดในครึ่งแรกมาได้สองลูกในครึ่งหลังจากมาริโอ มานด์ซูคิชและฟร็องค์ ริเบรี่ เอาชนะฮันโนเวอร์ไปได้ 2-0 แซงโบรุสเซีย ดอร์ทมุนด์ ขึ้นไปนำเป็นจ่าฝูงชั่วคราวก่อน<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><img src=\\"http://image.ohozaa.com/i/ee6/60Wu0q.png\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"><strong style=\\"margin: 0px; padding: 0px;\\">&nbsp;<span style=\\"margin: 0px; padding: 0px; color: rgb(165, 42, 42);\\">บุนเดสลีกา เยอรมัน</span></strong><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: rgb(165, 42, 42);\\">สนาม : อัลลิอันซ์ อารีน่า</span></strong><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: rgb(165, 42, 42);\\">วันเสาร์ที่ 14 กันยายน 2556&nbsp;</span></strong><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><img src=\\"http://image.ohozaa.com/i/e59/WfJg6X.png\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: blue;\\">บาเยิร์น มิวนิค 2:0 ฮันโนเวอร์</span></strong><img src=\\"http://image.ohozaa.com/i/23b/SNt1k7.png\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: blue;\\">ประตู</span></strong>&nbsp;: 1-0 มานด์ซูคิช น.51, 2-0 ริเบรี่ น.64<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: rgb(165, 42, 42);\\">ครึ่งแรก</span></strong><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">ริเบรี่ประเดิมซัด</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 3 บาเยิร์น มิวนิค เจ้าถิ่นลุยก่อนตามคาดจากจังหวะที่อาร์เยน ร็อบเบน เลี้ยงอยู่มุมเขตโทษฝั่งขวาพยายามลุยฝ่ากองหลังฮันโนเวอร์โดนสกัดมาหน้าเขตโทษเข้าทางฟร็องค์ ริเบรี่ วิ่งมาซัดเรียดแต่ยังตรงตัวรอน โรเบิร์ต ซีเลอร์ นายด่านทีมเยือน<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">มุลเลอร์หลบโกล์ได้แต่ยิงออก!</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 7 \\"เสือใต้\\" น่าได้ประตูขึ้นนำสุดๆจากจังหวะจ่ายบอลทะลุช่องของร็อบเบน มาให้โธมัส มุลเลอร์ วิ่งโฉบมารับบอลแถมแตะบอลตัดหน้าหนีมือซีเลอร์ ได้แล้ว แต่จังหวะจะยิงนั้นวางเท้าไม่ถนัดเลยแปเบี้ยวเข้าทางตาข่ายไปอย่างน่าเสียดาย&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">เกือบเฮง!อันเดรียเซ่นสกัดเกือบเสียบคานตัวเอง</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 17 เกือบกลายเป็นโชคดีของเจ้าบ้านจากจังหวะของมุลเลอร์เลี้ยงทางขวาจะจ่ายเข้ามาตรงกลางแต่โดนลีออน อันเดรียเซ่น สกัดบอลแต่เปลี่ยนทางไปหน้าประตูตัวเอง บอลย้อยเกือบเสียบคานแต่ซีเลอร์ยังถอยกลับมาปัดทิ้งไปได้ทัน<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">ฮันโนเวอร์ได้เสียวบ้าง</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 19 หลังจากช่วง 10 นาทีแรกมีลุ้นยิงแต่ไม่ใกล้เคียงแต่จังหวะนี้ได้ลุ้นเสียวบ้างแล้วจากจังหวะของเลโอนาร์โด้ บิตเท่นคอร์ท ได้บอลนอกกรอบเยื้องขวารอจังหวะหาช่องซัดไกลบอลพุ่งกระดอนพื้นแต่ยังเฉี่ยวเสาออกไป<br style=\\"margin: 0px; padding: 0px;\\"><img src=\\"http://image.ohozaa.com/i/eba/dxE6Br.jpg\\" border=\\"0\\" hspace=\\"10\\" vspace=\\"5\\" align=\\"right\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">อูยยย...โซลเบียคโฉบเตะมุมเสียวแว๊บ</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 24 ประมาทไม่ได้เลยสำหรับบาเยิร์น เสียเตะมุมให้ฮันโนเวอร์ทางขวา เอ็ดการ์ พริบเปิดโค้งมาเสาแรกเข้าอาร์ตู โซลเบียค โฉบมาโหม่งเปลี่ยนทางไปเสาสองหมดสิทธิ์สำหรับนอยเออร์แล้ว แต่บอลผ่านเสาไกลออกไปไม่มีใครพุ่งมาทัน บาเยิร์นรอดไปอย่างหวุดหวิด<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">มาอีก..โพค็อกโนลี่ตะบันไกลโด่งไป</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 26 จังหวะบุกสวนกลับของทีมเยือนมาอีกคราวนี้เป็นเซบาสเตียง โพค็อกโนลี่ แบ็คซ้ายเบลเยี่ยมเติมขึ้นมาก่อนกดด้วยซ้าย บอลพุ่งน่ากลัวแต่หายไปในกลุ่มแฟนด้านหลังโกล์แถมกรรมการยังย้อนจากจังหวะที่ฮันโนเวอร์โดนตัดฟาล์วแต่ให้ได้เปรียบ<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">เกือบได้ทั้งคู่!มานด์ซูคิชชาร์จวืด-ฮันโนเวอร์โต้เกือบได้จบ</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 38 จังหวะของบาเยิร์นพลาดไปอย่างน่าผิดหวังจากจังหวะของมุลเลอร์ หลุดมาทางขวามีเวลาเหลือเฟือแต่ดันจ่ายเข้ากลางให้มานด์ซูคิชเข้าชาร์จไม่ทันอีก แถมตัดกลับมาทีมเยือนโต้กลับของลาร์ส สตินเดิ้ล ก่อนจ่ายทะลุช่องหวังให้เอ็ดการ์ พริป ปีกซ้ายแต่นอยเออณ์ยังอ่านเกมทันวิ่งออกมาคว้าไว้จากเท้าได้อย่างยอดเยี่ยม<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">เสือเกือบหงอย!โคนานซัดแฉลบชนสามเหลี่ยม</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 40 ดูเหมือนไม่ใช่เกมบุกข้างเดียวของเจ้าถิ่นแล้วจากจังหวะมาทางซ้ายของยา โคนาน ดึงจังหวะหลอกราฟินญ่าก่อนล็อกเข้ายิงไปแฉลบโซลเบียค เพื่อนร่วมทีมแต่พุ่งไปที่สามเหลี่ยมเสาแรก นอยเออร์บินสุดตัวแต่ไม่ถึง โชคดีที่บอลชนคานเต็มๆ รอดพ้นการเสียประตูไปอีกหน&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">มานด์ซูคิชลุ้นโขกตรงโกล์</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 42 บาเยิร์นหวังจะยิงขึ้นขำให้ได้ก่อนจบครึ่งแรกจากการเติมขึ้นมาของดาวิด อลาบา ทางกราบซ้ายเปิดเข้ากลางให้มานด์ซูคิชเทคตัวโหม่งเต็มแต่ยังไปตรงตัวซีเลอร์รับไว้ได้<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\">จบครึ่งแรก บาเยิร์นที่แม้จะเกือบ 70 เปอร์เซ็นต์กลายเป็นทีมเยือนที่ได้เสียวเกือบได้ประตูมากกว่า ชานคานไปหนึ่งครั้ง ส่วนบาเยิร์นยังไม่คมในการจบสกอร์แถมทั้งร็อบเบนและริเบรี่ ตัวความหวังของพวกเขายังเล่นไม่ออก ทำให้ยังเสมอกันอยู่ 0-0<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: rgb(165, 42, 42);\\">ครึ่งหลัง</span></strong><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">หลังหายไปไหน!บิตเทนคอร์ทยืนยิงโล่งแต่ตามมาบล็อกทัน</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 50 ไม่รู้นักเตะบาเยิร์นเทไปปิดแต่เสาแรกแต่ปล่อยให้ลูกเปิดทางซ้ายของสตินเดิล ข้ามมาเสาสองให้บิตเทนคอร์ทวิ่งมาวอลเลย์เน้นๆแต่ บัวเต็งไหวตัวทันตามบล็อคช่วยไว้ทันอย่างหวุดหวิด<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">พี่เสือโชว์ทีมเวิร์ค!ร็อบเบนนำ-โครสชง-มานด์ซูคิชจบ 1-0</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 51 จังหวะทำเกมอันสวยงามของบาเยิร์นมาได้ผลจนได้จากการได้บอลของร็อบเบน ดึงหนีนักเตะฮันโนเวอร์ได้เยี่ยมแล้วจ่ายไปทางซ้ายให้โทนี่ โครส ที่สิ่งเติมจ่ายเข้ากลางจังหวะเดียวกลับมาให้มานด์ซูคิชบรรจงแปเข้าไปอย่างเฉียบขาด เป็นทีมวิ์คอันยอดเยี่ยมของ เสือใต้ ได้ประตูปลดล็อกนำ 1-0 แล้ว<br style=\\"margin: 0px; padding: 0px;\\"><img src=\\"http://image.ohozaa.com/i/ea0/ZzPrcv.jpg\\" border=\\"0\\" hspace=\\"10\\" vspace=\\"5\\" align=\\"left\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">ฟาน บุยเต็นขึ้นมาวอลเล่ย์ได้ลุ้น</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 53 จังหวะลุ้นลูกที่สองทันทีของเจ้าบ้านจากลูกเปิดเตะมุมของริเบรี่ ย้อนมาที่เส้นเขตโทษให้แดเนี่ยล ฟาน บุยเต็นเอี้ยวตัววอลเลย์แต่ซัดบอลเด้งพื้นแรงไปหน่อยเลยกระดอนข้ามคานไป<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">บัวเต็งเกือบงามหน้า..สกัดลูกยิงกากเกือบตุงตัวเอง</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 57 เยโรม บัวเต็ง ทำแฟนบาเยิร์นเสียวไส้สุดๆเมื่อจังหวะบุกของฮันโนเวอร์มาทางกราบซ้ายให้เอ็ดการ์ พริบ เลี้ยงหลอกล่อราฟินญ่าก่อนกดเรียดด้วยซ้าย บอลไม่ตรงกรอบแน่นอนแต่บัวเต็งดันยื่นขาสกัดบอลเปลี่ยนทางเฉียดเสาไปไม่ไกล เกือบทำเพื่อนเงิบทั้งทีม<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">มานด์ซูคิชโหม่งชงเยี่ยม-มุลเลอร์ยิงไปไหน</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 59 มาอีกครั้งจากการลุ้นเติมของอลาบา เปิดครอสข้ามมาเสาไกลให้มานด์ซูคิชโหม่งชงเข้ากลางให้มุลเลอร์ที่ยืนอยู๋กลางเขตโทษคนเดียวโล่งๆ แต่<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">บอลมันเป็นใจ!ร็อบเบนซัดติดเซฟ-ริเบรี่แปซ้ำไม่เหลือ 2-0</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 64 การแลกกันยิงดูแล้วจะเป็นบาเยิร์นที่ทำได้ดีกว่าตามคาด คราวนี้เป็นร็อบเบนที่อาศัยความเร็ววิ่งแซงมาร์เซโล่ขาดวิ่งจกาการจ่ายทะลุช่องทางซ้ายก่อนยิงโดนซีเลอร์เซฟช็อตแรกได้ แต่บอลไปเข้าทางริเบรี่วิ่งเข้าแปเรียดเข้าไปอย่างเยือกเย็นสมราคานักเตะยอดเยี่ยมของยุโรป บาเยิร์นทิ้งห่างเป็น 2-0 แล้ว<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">โคนานโหม่งเยี่ยมเกือบตีไข่แตก</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 65 หลังโดนลูกที่สองไป ฮันโนเวอร์ไม่ยอมแพ้เกือบได้ลูกตีไข่แตกจากการโหม่งของยา โคนาน พุ่งโหม่งเน้นๆเข้ามุมเขตเสาแรกแต่นอยเออร์พุ่งมาปัดออกไปได้อย่างยอดเยี่ยม<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">ซีเลอร์เหนื่อยต่อ..เซฟลูกยิงมุลเลอร์สวย</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 73 ยังต้องออกแรงกันต่อสำหรับนายประตูทีมเยือน คราวนี้เป็นมุลเลอร์ที่ได้บอลหน้ากรอบพอมีช่องให้ได้กดเต็มๆด้วยซ้ายน่าลุ้นแต่ซีเลอร์ยังล้มตะปบทิ้งไปได้อย่างยอดเยี่ยมเช่น<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: green;\\">น่าได้ลูกสาม..ร็อบเบนแปเน้นๆโดนบล็อคเหลือเชื่อ</span></strong><br style=\\"margin: 0px; padding: 0px;\\">นาที 76 ชวดได้ประตูปิดกล่องอย่างเหลือเชื่อจากการลุยของริเบรี่จนหนีกองหลัง 2 คนมาได้ก่อนเปิดย้อนเบาๆกลับมาให้ร็อบเบนได้เล็งแปเน้นๆกะย้อนไปทางเสาไกลเข้าแน่ๆแต่ซาเน่ เป็นฮีโร่สไลด์มาบล็อกไว้ทันอย่างสุดยอดทำเอาร็อบเบนเสียดายสุดๆ<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\">ช่วงท้าย มานด์ซูคิช ได้ลุ้นยิงลูกที่สามให้เจ้าบ้านแต่ยิงหลุดกรอบไป จบเกม บาเยิร์น ที่เกือบเอาตัวไม่รอดในครึ่งแรกมาได้สองลูกในครึ่งหลัง เอาชนะฮันโนเวอร์ไปได้ 2-0 แซงโบรุสเซีย ดอร์ทมุนด์ ขึ้นไปนำเป็นจ่าฝูงชั่วคราวก่อน&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: blue;\\">รายชื่อนักเตะทั้งสองทีม</span></strong><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: blue;\\">บาเยิร์น มิวนิค</span></strong>(4-2-3-1): มานูเอล นอยเออร์ 6 - ราฟินญ่า 6, เยโรม บัวเต็ง 5(ดานเต้ น.68 6), แดเนี่ยล ฟาน บุยเต็น 7, ดาวิด อลาบา 7 - ฟิลิปป์ ลาห์ม 7, โทนี่ โครส 7<img src=\\"http://i1.goal.com/web/goal/2013.08.21-v2/images/default/match/assists_l.gif\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\">&nbsp;<img src=\\"http://ligikuu.co.tz/2012-2013/images/yellow_card.gif\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\">&nbsp;(แยน เคิร์ชฮอฟฟ์ น.86) - อาร์เยน ร็อบเบน 8, โธมัส มุลเลอร์ 8, ฟร็องค์ ริเบรี่ 6<img src=\\"http://img26.elex-tech.org/2/2012/0613/7c/c/06921/20x20x100x0.jpg\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\">&nbsp;<br style=\\"margin: 0px; padding: 0px;\\"><img src=\\"http://ligikuu.co.tz/2012-2013/images/yellow_card.gif\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\">(เซอร์ดาน ชาคิรี่ น.81)- มาริโอ มานด์ซูคิช 7<img src=\\"http://img26.elex-tech.org/2/2012/0613/7c/c/06921/20x20x100x0.jpg\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\">โค้ช : เป๊บ กวาร์ดิโอล่า<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\"><strong style=\\"margin: 0px; padding: 0px;\\"><span style=\\"margin: 0px; padding: 0px; color: blue;\\">ฮันโนเวอร์</span></strong>&nbsp;(4-4-2): รอน โรเบิร์ต ซีเลอร์ 5 - ฮิโรกิ ซาไก 5.5, มาร์เซโล่ 5.5<img src=\\"http://ligikuu.co.tz/2012-2013/images/yellow_card.gif\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px;\\">, ซาลิฟ ซาเน่ 5.5, เซบาสเตียง โพคอกโนลี่ 5.5 - ลาร์ส สตินเดิล 6, ลีออน อันเดรียเซ่น 5.5(คริสเตียน ชูลซ์ น.74), เลโอนาร์โด้ บิตเทนคอร์ท 6, เอ็ดการ์ พริบ 5(แยน ชลอดราฟฟ์ น.82) - อาร์ตู โซลเบียค 5.5, ดิดิเยร์ ยา โคนาน 5.5(มานูเอล ชมีเดอบัค น.67)<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\">โค้ช : เมียร์โก้ สลอมก้า</span><br style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 17px;\\"><br style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 17px;\\"><img src=\\"http://image.ohozaa.com/i/50d/j2cOw9.jpg\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px; color: rgb(85, 85, 85); font-size: 17px;\\"><br style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 17px;\\"><img src=\\"http://image.ohozaa.com/i/f9e/CXkNC9.jpg\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px; color: rgb(85, 85, 85); font-size: 17px;\\"><img src=\\"http://image.ohozaa.com/i/2e7/n8K0q5.jpg\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px; color: rgb(85, 85, 85); font-size: 17px;\\"><img src=\\"http://image.ohozaa.com/i/758/H8qkeF.jpg\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px; color: rgb(85, 85, 85); font-size: 17px;\\"><img src=\\"http://image.ohozaa.com/i/71a/5SWooE.jpg\\" alt=\\"\\" style=\\"margin: 0px; padding: 0px; border: none; max-width: 909px; color: rgb(85, 85, 85); font-size: 17px;\\"></p></div>', '2013919155208-04.jpg', 1, '2013-09-19 15:52:08', 0);
INSERT INTO `post` VALUES (85, 5, 'ผลบอล ดอร์ทมุนด์ 6-2 ฮัมบูร์ก', 'ดอร์ทมุนด์ยังคงความร้อนแรงในเกมรุกอย่างต่อเนื่อง ช่วยกันยิงกันได้ถ้วนหน้า พาทีมเปิดบ้านถล่มฮัมบูร์ก หมดสภาพ 6-2 ชนะเป็นนัดที่ 5 กลับขึ้นเป็นจ่าฝูงแบบหล่อๆต่อไปอีกหนึ่งสัปดาห์', '<div align=\\"center\\" style=\\"font-family: Tahoma; font-size: 12px; text-align: -webkit-center;\\"><img src=\\"http://image.hugball.com/Pic/Match/12314_1.jpg\\" width=\\"300\\" height=\\"200\\"></div><div align=\\"left\\" style=\\"font-family: Tahoma; font-size: 12px; margin-left: 10px; margin-right: 10px;\\">&nbsp;&nbsp;&nbsp;&nbsp;<p>&nbsp;<strong style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 18px;\\"><span style=\\"margin: 0px; padding: 0px; color: blue;\\">เสือเหลืองโหดจัด!ยิงได้ถ้วนหน้าชำเราฮัมบูร์ก 6-2 นำฝูงต่อ</span></strong><br style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 18px;\\"><br style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 18px;\\"><span style=\\"color: rgb(85, 85, 85); font-size: 18px;\\">ดอร์ทมุนด์ยังคงความร้อนแรงในเกมรุกอย่างต่อเนื่อง ช่วยกันยิงกันได้ถ้วนหน้า พาทีมเปิดบ้านถล่มฮัมบูร์ก หมดสภาพ 6-2 ชนะเป็นนัดที่ 5 กลับขึ้นเป็นจ่าฝูงแบบหล่อๆต่อไปอีกหนึ่งสัปดาห์</span><br style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 18px;\\"><br style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 18px;\\"><strong style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 18px;\\"><span style=\\"margin: 0px; padding: 0px; color: rgb(165, 42, 42);\\">บุนเดสลีกา เยอรมัน<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\">สนาม : ซิกนัล อิดูน่า พาร์ค<br style=\\"margin: 0px; padding: 0px;\\"><br style=\\"margin: 0px; padding: 0px;\\">วันเสาร์ที่ 14 กันยายน 2556&nbsp;</span></strong><br style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 18px;\\"><br style=\\"margin: 0px; padding: 0px; color: rgb(85, 85, 85); font-size: 18px;\\"><strong style=\\"mar