INSERT INTO `webboard` VALUES (9, 1, 3, 'บอลน่าจะมา', '<span style="color: rgb(85, 85, 85); font-family: Arial, Helvetica, sans-serif; font-size: 15px; line-height: 16.5px; background-color: rgb(255, 255, 255); font-weight: bold; ">เมื่อวานก็ถือว่าดีพอตัว บอลมาตามเป้าเกือบหมด เรามาดูกันที่วันนี้กันดีกว่า&nbsp;<br><br><br><br><br><br>ตัวแรกมองไปที่&nbsp;<span style="color: red; "><span style="font-size: 18px; line-height: normal; ">เลบานเต้</span></span>&nbsp;ต้องบอกว่าฮอตเหลือเกินสำหรับ เลบานเต้ในฤดูกาลนี้ เป็นทีมที่มีแนวรุกจัดจ้าน ไม่ว่าจะเป็น โอบาเฟมี่ มาร์ติน หรือ นาบิล เอลซาร์ ถึงแม้ เกมเยือนเลบานเต้จะไม่ค่อยดีนัก แต่มาเจอกับ เมลิลล่า ซึ่งอยู่ลีคต่ำชั้นกว่ามาก เชื่อว่า ฮวน อิกนาซิโอ มาติเนซ นายใหญ่ของเลบานเต้ต้องกำชับให้ลูกทีมเก็บชัยชนะให้ได้ เพราะ เลบานเต้เล่นหวังทุกรายการ มองไปที่ราคา - 0.5&nbsp;<br><br>ตัวที่สองให้มองไปที่&nbsp;<span style="font-size: 18px; line-height: normal; "><span style="color: blue; ">เกเฟล</span></span>&nbsp;มองโดยรวมแล้วเกเฟลแทบจะไม่ได้เหนือกว่ามากแถมไปเยือนอีก แต่เจ้าบ้าน คือ กาอิช ที่เกมรับห่วยแตกที่สุดในลีค เป็นทีมที่พร้อมจะแพ้ได้ทุกทีม เจ้าบ้านนั้นตกชั้นเรียบร้อยแล้วคงจะถอดใจไปแล้ว ส่วนทีมเยือนยังต้องการแต้มเพื่อเซฟตัวเองไม่ให้ตกชั้นเชื่อว่านัดนี้แรงจูงใจจะตกไปทางฝั่ง เกเฟลและเชื่อว่าเกเฟลดีพอที่จะมายิง กาอิช ถึงถิ่น มองไปที่ราคา - 0.75&nbsp;<br><br>ตัวสุดท้ายให้มองไปที่&nbsp;<span style="font-size: 18px; line-height: normal; "><span style="color: rgb(139, 0, 0); ">เซบีญ่า</span></span>&nbsp;ช่วงหลังเซบีญ่าเป็นทีมที่มีแนวรุกที่พร้อมจะทำประตูได้เสมอ ถึงจะเป็นบอลถ้วย แต่ด้วยสปิริตทีมที่ต้องเจอกับเอสปันย่อล ในลีคเดียวกันแล้ว ยังไงเซบีญ่า ก็ต้องการชนะหากหวังเข้ารอบต่อไป มองไปที่ราคา - 1</span><span style="color: rgb(85, 85, 85); font-family: Arial, Helvetica, sans-serif; font-size: 15px; line-height: 16.5px; background-color: rgb(255, 255, 255); ">&nbsp;</span>', '', '2012-11-1 13:10:59', 7, 0, '127.0.0.1', '2012-11-01 13:10:59', 1);
